var btnLogin=document.querySelector(".btn-login");btnLogin.addEventListener("click",e=>{e.preventDefault();var n=document.querySelector(".email"),t=document.querySelector(".password"),r=n.value,l=t.value,o=document.querySelectorAll(".input-group span");if(o.length)for(let e=0;e<o.length;e++)o[e].remove();document.querySelectorAll(".form");if(!r){let e=document.createElement("span");e.innerHTML="Invalid email",document.querySelector(".email-group").append(e)}if(!l){let e=document.createElement("span");e.innerHTML="Invalid password",document.querySelector(".password-group").append(e)}if(r&&l){let e=document.createElement("h1");e.innerHTML="Login Successfully!",document.querySelector("form").prepend(e),n.value="",t.value=""}});